import { useState, useEffect } from 'react';
import { Route, Routes } from 'react-router-dom'
import Dashboard from './components/Dashboard';
import Home from './components/Home';
import Login from './components/Login';
import Logout from './components/Logout';
import Navbar from './components/Navbar';
import Register from './components/Register';
import Protected from './Protected';




function App() {
  const [isLoggedIn, setisLoggedIn] = useState(true);
  const [isnotLoggedIn, setisnotLoggedIn] = useState(false);

  const LoginRoute = async () => {
    try {
      const res = await fetch('/auth', {
        method : "GET",
        headers : {
          Accept : "application/json",
          "Content-Type" : "application/json"
        },
        credentials : "include"
      });

      if(res.status === 200){
        setisLoggedIn(false)
        setisnotLoggedIn(true)
      }if(res.status ===401){
        setisLoggedIn(true)
        setisnotLoggedIn(true)
      }
    } catch (error) {
      console.log(error)
    }
  }

  useEffect(() => {
    LoginRoute();
  }, []);

  return (
    <><Navbar isLoggedIn={isLoggedIn} /><Routes>
      <Route path='/' element={<Home />}></Route>

      <Route
        path="/Login"
        element={
          <Protected isnotLoggedIn={isnotLoggedIn} component={Login}>
            <Login />
          </Protected>
        }
      />

<Route
        path="/Register"
        element={
          <Protected isnotLoggedIn={isnotLoggedIn} component={Register}>
            <Register />
          </Protected>
        }
      />

      <Route
        path="/Logout"
        element={
          <Protected isLoggedIn={isLoggedIn} component={Logout}>
            <Logout />
          </Protected>
        }
      />

      <Route
        path="/Dashboard"
        element={
          <Protected isLoggedIn={isLoggedIn} component={Dashboard}>
            <Dashboard />
          </Protected>
        }
      />


    </Routes></>

  );
}

export default App;
