import React,{useEffect,useState} from 'react'
import axios from "axios";


function Dashboard() {
  const local = localStorage.getItem('email');
const [email, setEmail] = useState(local)
const [list, setList] = useState([]);
const [username, setUsername] = useState("")

useEffect(() => {
  axios.post("/api/user/",{
    email,
  }).then((res)=>{
    console.log(res)
    setList(res.data)
    localStorage.setItem('username',username)
  })
}, [])

  
  return (
   <div>
    <section id="Dashboard">
            <div className='container'>
                <div className='row justify-content-center'>
                    <div className='col-md-8 mt-5'>
                        <h1 className='display-4 fw-bolder mb-4 text-center text-white'>Hello '{list.username}</h1>
                        <p className='lead text-center fs-4 mb-5 text-white'>Paragraphs are medium-sized units of writing, longer than sentences, but shorter than sections, chapters, or entire works. Because they connect the “small” ideas of individual sentences to a “bigger” idea, paragraph structure is essential to any writing for organization, flow, and comprehension. </p>
                        
                    </div>
                </div>
            </div>
        </section>

  </div>
    
  )
}

export default Dashboard
