import {Route,Routes} from 'react-router-dom'
import Dashboard from './components/Dashboard';
import Home from './components/Home';
import Login from './components/Login';
import Logout from './components/Logout';
import Register from './components/Register';
import Protectedroute from './components/Protectedroute'
import NAVBAR from "./components/Navbar"



function App() {
  const [auth, setauth] = useState(false);
  const [auth1, setauth1] = useState(true);

  const isLoggedIn = async () => {
    try {
      const res = await fetch('/auth', {
        method : "GET",
        headers : {
          Accept : "application/json",
          "Content-Type" : "application/json"
        },
        credentials : "include"
      });

      if(res.status === 200){
        setauth(true)
        setauth1(false)
      }
      if(res.status === 401){
        setauth(false)
        setauth1(true)
      }

    } catch (error) {
      console.log(error)
    }
  }

  useEffect(() => {
    isLoggedIn();
  }, []);



  return (
    <><NAVBAR auth={auth1}/>

    <Routes>
      <Route path='/' element={<Home />}></Route>
      <Protectedroute path='/Login' element={<Login />} auth={auth1} />
      <Protectedroute path='/Register' element={<Register />} auth={auth1} />
      <Protectedroute path='/Dashboard' element={<Dashboard />} auth={auth} />
      <Protectedroute path='/Logout' element={<Logout />} auth={auth} />
    </Routes></>
  );
}

export default App;
